package com.class2;

public class Perimeter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		
		//Find the perimeter and the area of a rectangle with the width 5 and height 8
		
		double w=5;
		double h=8;
		
		double perimeter=2*(w+h);
		double area=w*h;
		
		
		System.out.println("The perimeter of a rectangle with the width "+w+" and the height "+h+" is "+perimeter+"");
		System.out.println("The area of a rectangle with the width "+w+" and the height "+h+" is "+area+"");
		
		
		
		
		//SHORT CUTS
		//type Syso then hold control and hit space bar. hit enter to go all the way
		
		
		
	}

}
