package com.class2;

public class SquareRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		// Find the square of the number 3.9
		double f = 3.9;

		double squ = f * f;

		System.out.println(squ);

		System.out.println("The square of the number " + f + " is " + squ + "");
		
		
	}

}
