package com.class2;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Variable student information

		String firstName = "Wilberth";
		String lastName = "Suarez";
		char grade = 'A';
		String city = "Waldorf";
		String state = "MD";
		String phoneNumber = "347-555-1620";

		System.out.println(firstName);
		System.out.println(lastName);
		System.out.println(grade);
		System.out.println(city);
		System.out.println(state);
		System.out.println(phoneNumber);

		System.out.println("My name is " + firstName + " and my last name is " + lastName + ". I am a " + grade
				+ " student and I live in " + city + " " + state + ". My phone number is " + phoneNumber + ".");
		
		
		System.out.println("My first name is "+firstName+"");
		System.out.println("My last name is "+lastName+"");
		System.out.println("My grade is an "+grade+"");
		System.out.println("I live in "+city+", "+state+"");
		System.out.println("My phone number is "+phoneNumber+"");
		
		

	}

}
