package com.class3;

public class Modules {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=3;
		int div=a/b;
		
		System.out.println(div);
		
		//modulus Operator
		//the remainder of 10/3 will be 1 bc 3*2=9 and 1 left over.
		//this is how you show the remainder for the equation
		
		int mod=a%b;
		System.out.println(mod);
		
		int q=47;
		int h=5;
		System.out.println(q%h);

		//PEMDAS
		
		int e=-5+4*6;
		System.out.println(e);
		
		int x=(22+9) % 7;
		System.out.println(x);
	}

}
