package com.class8;

public class Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int sum=0;
		for (int i=1;		i<=5;		i++) {
			sum= sum+i;
	}
		System.out.println(sum);
	
	int sumAll=0;
	for (int y=10;		y<=50;			y+=10) {
		sumAll= sumAll+y;
		System.out.println(sumAll);
	}
	}

	
}
